<?php
 namespace App\Http\Controllers\Api; use App\Http\Controllers\Controller; use App\Models\AttendanceLog; use App\Models\Device; use App\Models\Employee; use Illuminate\Http\Request; use Illuminate\Support\Carbon; use Illuminate\Support\Facades\Log; use Illuminate\Support\Facades\Validator; use Illuminate\Validation\Rule; class AttendanceLogController extends Controller { public function handleAiRequest(Request $request) { $validator = Validator::make($request->all(), [ 'device_api_key' => ['required', 'string'], 'personnel_code' => ['required', 'string', 'exists:employees,personnel_code'], 'event_type' => ['required', 'string', Rule::in([AttendanceLog::TYPE_CHECK_IN, AttendanceLog::TYPE_CHECK_OUT])], 'timestamp' => ['required', 'date_format:Y-m-d H:i:s'], 'source_name'=> ['nullable', 'string'], ]); if ($validator->fails()) { return response()->json(['errors' => $validator->errors()], 422); } $validated = $validator->validated(); $device = Device::where('api_key', $validated['device_api_key'])->first(); if (!$device) { return response()->json(['message' => 'Invalid API Key.'], 401); } if ($device->status !== 'online') { return response()->json(['message' => 'Device is not active.'], 403); } $employee = Employee::where('personnel_code', $validated['personnel_code'])->first(); if (!$employee) { return response()->json(['message' => 'Invalid User personnel_code.'], 401); } $logTimestamp = Carbon::parse($validated['timestamp']); $scheduleToday = $employee->getWorkScheduleFor($logTimestamp); $scheduleYesterday = $employee->getWorkScheduleFor($logTimestamp->copy()->subDay()); $schedule = null; $diffToday = 999999; $diffYesterday = 999999; if ($validated['event_type'] == AttendanceLog::TYPE_CHECK_IN) { if ($scheduleToday && $scheduleToday->expected_start) { $diffToday = abs($logTimestamp->diffInMinutes($scheduleToday->expected_start)); } if ($scheduleYesterday && $scheduleYesterday->expected_start) { $diffYesterday = abs($logTimestamp->diffInMinutes($scheduleYesterday->expected_start)); } } else { if ($scheduleToday && $scheduleToday->expected_end) { $diffToday = abs($logTimestamp->diffInMinutes($scheduleToday->expected_end)); } if ($scheduleYesterday && $scheduleYesterday->expected_end) { $diffYesterday = abs($logTimestamp->diffInMinutes($scheduleYesterday->expected_end)); } } if ($diffYesterday < $diffToday) { $schedule = $scheduleYesterday; } else { $schedule = $scheduleToday; } $lastLog = AttendanceLog::where('employee_id', $employee->id) ->where('timestamp', '<', $logTimestamp) ->orderBy('timestamp', 'desc') ->first(); $hasPriorCheckIn = false; if ($schedule && $schedule->expected_start) { $hasPriorCheckIn = AttendanceLog::where('employee_id', $employee->id) ->where('timestamp', '>=', $schedule->expected_start->copy()->subHours(4)) ->where('timestamp', '<', $logTimestamp) ->where('event_type', AttendanceLog::TYPE_CHECK_IN) ->exists(); } $lateness_minutes = 0; $early_departure_minutes = 0; if ($schedule) { $expectedStart = $schedule->expected_start; $expectedEnd = $schedule->expected_end; $floatingStart = $schedule->floating_start ?? 0; $floatingEnd = $schedule->floating_end ?? 0; if ($validated['event_type'] == AttendanceLog::TYPE_CHECK_IN && $expectedStart) { if ($lastLog && $lastLog->event_type == AttendanceLog::TYPE_CHECK_OUT) { if ($lastLog->early_departure_minutes > 0) { $lastLog->early_departure_minutes = 0; $lastLog->save(); } $lastExitTime = Carbon::parse($lastLog->timestamp); $gapMinutes = $lastExitTime->diffInMinutes($logTimestamp); $lateness_minutes = $gapMinutes; } elseif (!$hasPriorCheckIn) { if ($logTimestamp->gt($expectedStart)) { $diffInMinutes = $expectedStart->diffInMinutes($logTimestamp); if ($diffInMinutes > $floatingStart) { $lateness_minutes = $diffInMinutes; } } } } elseif ($validated['event_type'] == AttendanceLog::TYPE_CHECK_OUT && $expectedEnd) { Log::info("the exit time is: " . $expectedEnd->diffInMinutes($logTimestamp)); if ($logTimestamp->lt($expectedEnd)) { Log::info("the exit time is2 : " . $expectedEnd->diffInMinutes($logTimestamp)); Log::info("the expectedEnd time is: " . $expectedEnd); Log::info("the logTimestamp is: " . $logTimestamp); $diffInMinutes = abs($expectedEnd->diffInMinutes($logTimestamp)); if ($diffInMinutes > $floatingEnd) { $early_departure_minutes = $diffInMinutes; } } } } $log = AttendanceLog::create([ 'employee_id' => $employee->id, 'timestamp' => $logTimestamp->toDateTimeString(), 'event_type' => $validated['event_type'], 'device_id' => $device->id, 'source_name' => $validated['source_name'] ?? $device->name, 'source_type' => 'auto', 'lateness_minutes' => $lateness_minutes, 'early_departure_minutes' => $early_departure_minutes, 'remarks' => $validated['source_name'], ]); return response()->json([ 'message' => 'Attendance log created successfully', 'log_id' => $log->id, 'calculated_lateness' => $lateness_minutes, 'calculated_early_departure' => $early_departure_minutes, 'matched_schedule_start' => $schedule ? $schedule->expected_start->toDateTimeString() : null ], 201); } }