<?php
 namespace App\Http\Resources; use Illuminate\Http\Request; use Illuminate\Http\Resources\Json\JsonResource; class UserResource extends JsonResource { public function toArray(Request $request): array { return [ 'id' => $this->id, 'user_name' => $this->user_name, 'email' => $this->email, 'email_verified_at' => $this->email_verified_at, 'created_at' => $this->created_at, 'updated_at' => $this->updated_at, 'roles' => $this->whenLoaded('roles', fn() => $this->roles->pluck('name')), 'employee' => new EmployeeResource($this->whenLoaded('employee')), ]; } } 