<?php
 namespace App\Models; use GeneaLabs\LaravelModelCaching\Traits\Cachable; use Illuminate\Database\Eloquent\Factories\HasFactory; use Illuminate\Database\Eloquent\Model; use Illuminate\Database\Eloquent\Relations\BelongsTo; class EmployeeShift extends Model { use HasFactory,Cachable; protected $fillable = [ 'employee_id', 'date', 'work_pattern_id', 'is_off_day', 'shift_schedule_id', 'source', 'expected_start_time', 'expected_end_time', ]; protected $casts = [ 'date' => 'date', 'is_off_day' => 'boolean', 'expected_start_time' => 'datetime', 'expected_end_time' => 'datetime', ]; public function employee(): BelongsTo { return $this->belongsTo(Employee::class); } public function workPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class); } public function shiftSchedule(): BelongsTo { return $this->belongsTo(ShiftSchedule::class); } } 