<?php
 namespace App\Models; use Illuminate\Database\Eloquent\Factories\HasFactory; use Illuminate\Database\Eloquent\Model; use Illuminate\Database\Eloquent\Relations\BelongsTo; use Illuminate\Database\Eloquent\Relations\HasMany; use Staudenmeir\LaravelAdjacencyList\Eloquent\HasRecursiveRelationships; class LeaveType extends Model { use HasFactory,HasRecursiveRelationships; protected $fillable = [ 'name', 'parent_id', 'description', ]; public function parent(): BelongsTo { return $this->belongsTo(LeaveType::class, 'parent_id'); } public function children(): HasMany { return $this->hasMany(LeaveType::class, 'parent_id'); } public function allChildren(): HasMany { return $this->children()->with('allChildren'); } public function leaveRequests(): HasMany { return $this->hasMany(LeaveRequest::class); } } 