<?php
 namespace App\Models; use GeneaLabs\LaravelModelCaching\Traits\Cachable; use Illuminate\Database\Eloquent\Factories\HasFactory; use Illuminate\Database\Eloquent\Model; use Illuminate\Database\Eloquent\Relations\BelongsTo; use Illuminate\Database\Eloquent\Relations\HasMany; class WeekPattern extends Model { use HasFactory,Cachable; protected $fillable = [ 'name', 'saturday_pattern_id', 'sunday_pattern_id', 'monday_pattern_id', 'tuesday_pattern_id', 'wednesday_pattern_id', 'thursday_pattern_id', 'friday_pattern_id', 'floating_end', 'floating_start', ]; public function saturdayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'saturday_pattern_id'); } public function sundayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'sunday_pattern_id'); } public function mondayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'monday_pattern_id'); } public function tuesdayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'tuesday_pattern_id'); } public function wednesdayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'wednesday_pattern_id'); } public function thursdayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'thursday_pattern_id'); } public function fridayPattern(): BelongsTo { return $this->belongsTo(WorkPattern::class, 'friday_pattern_id'); } public function workGroups(): HasMany { return $this->hasMany(WorkGroup::class); } public function employees(): HasMany { return $this->hasMany(Employee::class); } } 